import React, { useState } from "react";
import "./UserInput.css";

const UserInput = () => {
  // const [currentSavings, SetCurrentSaving] = useState("");
  // const [yearlySavings, SetYearlySavings] = useState("");
  // const [expectedInterest, setExpectedInterest] = useState("");
  // const [investmentDuration, SetInvestmentDuration] = useState("");

  useState({
    "current-savings": 10000,
  });

  const submitHandler = (event) => {
    event.preventDefault();
    const investments = {
      current: +currentSavings,
      yearly: +yearlySavings,
      expected: +expectedInterest,
      duration: +investmentDuration,
    };
  };

  const resetHandler = () => {
    console.log("reset");
  };

  const calculateHandler = () => {
    console.log("cal");
  };
  const changeHandler = (input, value) => {
    console.log(input, value);
  };
  //발생하는 input과 값을 입력하는 value 두개 파라미터를 작성해야한다 (현재 함수는 input의 공용함수임)

  return (
    <form onSubmit={submitHandler} className="form">
      <div className="input-group">
        <p>
          <label htmlFor="current-savings">Current Savings ($)</label>
          <input
            onChange={(event) =>
              changeHandler("current-savings", event.target.value)
            }
            /** changeHandler 함수에서 설정한 파라미터를 받기 위해서 화살표 함수로 작성함,
          함수를 프롭스로 땡겨와서, 해당 input의 ID 값인 current-savings을 가져오고, 이벤트가 실행되면 값을 받게 
          event.target.value로 작성함*/
            type="number"
            id="current-savings"
          />
        </p>
        <p>
          <label htmlFor="yearly-contribution">Yearly Savings ($)</label>
          <input
            onChange={(event) =>
              changeHandler("yearly-contribution", event.target.value)
            }
            type="number"
            id="yearly-contribution"
          />
        </p>
      </div>
      <div className="input-group">
        <p>
          <label htmlFor="expected-return">
            Expected Interest (%, per year)
          </label>
          <input
            onChange={(event) =>
              changeHandler("expected-return", event.target.value)
            }
            type="number"
            id="expected-return"
          />
        </p>
        <p>
          <label htmlFor="duration">Investment Duration (years)</label>
          <input
            onChange={(event) => changeHandler("duration", event.target.value)}
            type="number"
            id="duration"
          />
        </p>
      </div>
      <p className="actions">
        <button onClick={resetHandler} type="reset" className="buttonAlt">
          Reset
        </button>
        <button onClick={calculateHandler} type="submit" className="button">
          Calculate
        </button>
      </p>
    </form>
  );
};
export default UserInput;
